define(['jquery'],function($){return{initialise:function(){$('.checkall').click(function(){var form=$(this).parents(':eq(5)');form.find("input[type='checkbox']").prop('checked',true);form.find('select[name="actions"]').prop('disabled',false);});$('.uncheckall').click(function(){var form=$(this).parents(':eq(5)');form.find("input[type='checkbox']").prop('checked',false);form.find('select[name="actions"]').prop('disabled',true);});}}});